<?phpadd_filter('bf_admin_tabs', 'buddyforms_anonymous_admin_tab', 10, 1);function buddyforms_anonymous_admin_tab($tabs){	$tabs['anonymous'] = 'Anonymous Author';	return $tabs;}add_action( 'buddyforms_settings_page_tab', 'buddyforms_anonymous_settings_page_tab' );function buddyforms_anonymous_settings_page_tab($tab){	if($tab != 'anonymous')		return $tab;	$buddyforms_anonymous_author_settings = get_option( 'buddyforms_anonymous_author_settings' ); ?>	<h2><?php _e( 'Post Types Default Form', 'buddyforms' ); ?></h2>	<p>Select a default form for every post type.</p>	<p>This will make sure that posts created before BuddyForms will have a form associated. <br>		If you select none the post edit link will point to the admin for posts not created with		BuddyForms</p>	<form method="post" action="options.php">		<?php settings_fields( 'buddyforms_anonymous_author_settings' ); ?>		<table class="form-table">		<tr valign="top">			<th scope="row" valign="top">				<?php _e('Select Anonymous Author')?>			</th>			<td>				<input type="text" value="<?php echo $buddyforms_anonymous_author_settings ?>" name="buddyforms_anonymous_author_settings">			</td>		</tr>			</tbody>		</table>		<?php submit_button(); ?>	</form>	<?php}function buddyforms_anonymous_register_option() {	// creates our settings in the options table	register_setting( 'buddyforms_anonymous_author_settings', 'buddyforms_anonymous_author_settings', 'buddyforms_anonymous_author_settings_default_sanitize' );}add_action( 'admin_init', 'buddyforms_anonymous_register_option' );function buddyforms_anonymous_author_settings_default_sanitize( $new ) {	return $new;}