or_init() {	require( dirname( __FILE__ ) . '/includes/form-elements.php' );	require( dirname( __FILE__ ) . '/includes/functions.php' );	if(is_admin()){		require( dirname( __FILE__ ) . '/includes/admin.php' );	}}require( dirname( __FILE__ ) . '/includes/author-taxonomies.php' );